

 module.exports = function(RED) {
    "use strict";

    function TriggerNode(n) {
        RED.nodes.createNode(this,n);


        this.subject = this.name
        this.filename = n.props;
        this.probs = JSON.parse(n.probs)

        this.on("input", function(msg, send, done) {

            
            msg ={
                subject: this.subject,
                filename: this.filename,
                probs: this.probs,
            }
            send(msg);
            done();
        });
    }

    RED.nodes.registerType("trigger",TriggerNode);


    RED.httpAdmin.post("/trigger/:id", RED.auth.needsPermission("trigger.write"), function(req,res) {
        var node = RED.nodes.getNode(req.params.id);
        if (node != null) {
            try {
                node.receive();
                res.sendStatus(200);
            } catch(err) {
                res.sendStatus(500);
                node.error(RED._("cause.failed",{error:err.toString()}));
            }
        } else {
            res.sendStatus(404);
        }
    });
}
