

 module.exports = function(RED) {
    "use strict";

    function AttributeNode(n) {
        RED.nodes.createNode(this,n);

        this.subject = n.name

        this.on("input", function(msg, send, done) {
            msg ={
                subject: this.name,
            }
            send(msg);
            done();
        });
    }

    RED.nodes.registerType("attribute", AttributeNode)
   
}